import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {
searchVar: any;

courses = [
  { name: 'Course 1', description: 'Description of Course 1' },
  { name: 'Course 2', description: 'Description of Course 2' },
  { name: 'Course 3', description: 'Description of Course 3' },
  // Add more courses as needed
];

searchVar: string = '';

get filteredCourses() {
  return this.courses.filter(course =>
    course.name.toLowerCase().includes(this.searchVar.toLowerCase()) ||
    course.description.toLowerCase().includes(this.searchVar.toLowerCase())
  );
}
}
<div class="home-page-banner">
    <div class="home-page-banner-slogan">
      <h1>Learn coding in easy step by step way. Excel in your carrer.</h1>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
        Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
        Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
      </p>
    </div>


    <!-- <div class="home-page-banner-search">
      <input type="text" class="search-input" placeholder="Search Courses" [(ngModel)]="searchVar">
      <button class="search-button" [routerLink]="['/course']" [queryParams]="{search: searchVar}">Search Courses</button>
    </div> -->

    <div class="home-page-banner-search">
      <input type="text" class="search-input" placeholder="Search Courses" [(ngModel)]="searchVar">
      <button class="search-button" [routerLink]="['/course']" [queryParams]="{search: searchVar}">Search Courses</button>
    </div>

    <ul>
      <li *ngFor="let course of filteredCourses">
        {{ course.name }} - {{ course.description }}
      </li>
    </ul>


  </div>
