import { Component, inject, OnInit } from '@angular/core';
import { Course } from '../Models/course';
import { CourseService } from '../Services/course.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.css']
})
export class CoursesComponent implements OnInit {

  coursesService = inject(CourseService);
  AllCourses: Course[] = this.coursesService.courses;
  searchString:string;

  activateRoute:ActivatedRoute = inject(ActivatedRoute)

  ngOnInit(){

    // this.searchString = this.activateRoute.snapshot.queryParams['search'];
    // this.searchString = this.activateRoute.snapshot.queryParamMap.get('search');
    // console.log(this.searchString);

    this.activateRoute.queryParamMap.subscribe((data) => {
      this.searchString = data.get('search');

      if(this.searchString === undefined || this.searchString === '' || this.searchString === null){
        this.coursesService.getAllcourses().subscribe((data: Course[]) => {
          this.AllCourses = data;
        });

        this.AllCourses = this.activateRoute.snapshot.data['courses'];
      }else{
        this.AllCourses = this.coursesService.courses
          .filter(x => x.title.toLowerCase()
          .includes(this.searchString.toLowerCase()));
      }
    })
  }
}
<div class="courses-page-main-container">
    <div class="courses-banner">
        <div class="courses-banner-layover">
            <h1>Empower your future with procademy's high quality online coding classes.</h1>
        </div>
    </div>
    <div class="courses-page-all-courses">
        <h2>Choose the course best suits you</h2>
        <hr>
        <div class="courses-page-course-list">
            <div class="course-card" *ngFor="let course of AllCourses">
                <div class="course-image">
                    <img [src]="course.image">
                </div>
                <div class="course-title">
                    {{ course.title }}
                </div>
                <div class="course-desc">
                    {{ course.desc.substring(0, 120) }}
                </div>
                <div class="course-price-rating">
                    <div class="course-price"><b>PRICE:</b> {{ course.price | currency }}</div>
                    <div class="course-rating"><b>RATINGS:</b> {{ course.rating }}</div>
                </div>
                <div class="course-action-buttons">
                    <button class="btn">BUY NOW</button>
                    <button class="btn" [routerLink]="'/Courses/Course/'+course.id">DETAILS</button>
                </div>
            </div>
            <div class="courses-page-course-list">
                <div class="course-card" *ngFor="let course of AllCourses">
                    <div class="course-image">
                        <img [src]="course.image">
                    </div>
                    <div class="course-title">
                        {{ course.title }}
                    </div>
                    <div class="course-desc">
                        {{ course.desc.substring(0, 120) }}
                    </div>
                    <div class="course-price-rating">
                        <div class="course-price"><b>PRICE:</b> {{ course.price | currency }}</div>
                        <div class="course-rating"><b>RATINGS:</b> {{ course.rating }}</div>
                    </div>
                    <div class="course-action-buttons">
                        <button class="btn">BUY NOW</button>
                        <button class="btn" [routerLink]="'/Courses/Course/'+course.id">DETAILS</button>
                    </div>
                </div>
            </div>
            <div class="courses-page-course-list">
                <div class="course-card" *ngFor="let course of AllCourses">
                    <div class="course-image">
                        <img [src]="course.image">
                    </div>
                    <div class="course-title">
                        {{ course.title }}
                    </div>
                    <div class="course-desc">
                        {{ course.desc.substring(0, 120) }}
                    </div>
                    <div class="course-price-rating">
                        <div class="course-price"><b>PRICE:</b> {{ course.price | currency }}</div>
                        <div class="course-rating"><b>RATINGS:</b> {{ course.rating }}</div>
                    </div>
                    <div class="course-action-buttons">
                        <button  class="btn">BUY NOW</button>
                        <button class="btn" [routerLink]="'/Courses/Course/'+course.id">DETAILS</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
